const embeddingString = $json.data;
const maxLen = 1900; // safety margin below 2000
const richText = [];

for (let i = 0; i < embeddingString.length; i += maxLen) {
    richText.push({
      text: {
        content: embeddingString.slice(i, i + maxLen),
      },
    });
  }

return {
  json: {
    pageId: $('Generate Checksum').item.json.pageId,
    "pageUpdateBody": {
      "properties": {
        "Embeddings": {
          "rich_text": richText
        },
        "Checksum": {
          "rich_text": [
            {
              "text": {
                "content": $('Generate Checksum').item.json.newChecksum
              }
            }
          ]
        }
      }
    }
  },
};

// Get query embedding from previous node (single item)
const queryEmbedding = JSON.parse($('Get Question Embeddings').first().json.data);

if (!Array.isArray(queryEmbedding) || queryEmbedding.length === 0) {
  throw new Error('queryEmbedding is missing or empty');
}

// Cosine similarity
function cosineSimilarity(a, b) {
  let dot = 0;
  let na = 0;
  let nb = 0;

  const len = Math.min(a.length, b.length);
  for (let i = 0; i < len; i++) {
    dot += a[i] * b[i];
    na += a[i] * a[i];
    nb += b[i] * b[i];
  }

  if (na === 0 || nb === 0) return 0;
  return dot / (Math.sqrt(na) * Math.sqrt(nb));
}

let best = null;

// Iterate over all prompts (Notion pages)
for (const item of $('Get All Prompts for search').all()) {
  const page = item.json;

  // 1) Read prompt text
  const promptText = (page.property_prompt);

  if (!promptText) {
    continue;
  }

  // 2) Rebuild embedding JSON string from rich_text chunks
  const embeddingChunks = JSON.stringify(page.property_embeddings);
  const embeddingString = page.property_embeddings;

  if (!embeddingString) {
    continue;
  }

  // 3) Parse embedding
  let embedding;
  try {
    embedding = JSON.parse(embeddingString);
  } catch (e) {
    continue; // skip broken embeddings
  }

  if (!Array.isArray(embedding) || embedding.length === 0) {
    continue;
  }

  // 4) Similarity
  const score = cosineSimilarity(queryEmbedding, embedding);

  if (!best || score > best.score) {
    best = {
      pageId: page.id,
      promptText,
      score,
    };
  }
}

// Threshold: if similarity is too low, treat as "not found"
const MIN_SCORE = 0.4; // tweak if needed

if (!best || best.score < MIN_SCORE) {
  return [{
    json: {
      prompt: null,
      notFound: true,
      score: best ? best.score : null,
    },
  }];
}

return [{
  json: {
    prompt: best.promptText,
    notFound: false,
    score: best.score,
    pageId: best.pageId, // optional, might be useful
  },
}];

// Pure JavaScript SHA-256 implementation (works in n8n sandbox)
function sha256(ascii) {
    function rightRotate(value, amount) {
        return (value >>> amount) | (value << (32 - amount));
    }

    const mathPow = Math.pow;
    const maxWord = mathPow(2, 32);
    const lengthProperty = 'length';
    const words = [];
    const asciiBitLength = ascii[lengthProperty] * 8;

    let hash = sha256.h = sha256.h || [];
    let k = sha256.k = sha256.k || [];
    let primeCounter = k[lengthProperty];

    const isPrime = num => {
        for (let i = 2, sqrt = Math.sqrt(num); i <= sqrt; i++) {
            if (num % i === 0) return false;
        }
        return true;
    };

    if (!primeCounter) {
        let candidate = 2;
        while (primeCounter < 64) {
            if (isPrime(candidate)) {
                hash[primeCounter] = (mathPow(candidate, .5) * maxWord) | 0;
                k[primeCounter++] = (mathPow(candidate, 1/3) * maxWord) | 0;
            }
            candidate++;
        }
    }

    ascii += '\x80'; // append Æ‡' bit
    while (ascii[lengthProperty] % 64 - 56) ascii += '\x00';
    for (let i = 0; i < ascii[lengthProperty]; i++) {
        const j = ascii.charCodeAt(i);
        words[i >> 2] |= j << ((3 - i) % 4) * 8;
    }
    words[words[lengthProperty]] = (asciiBitLength / maxWord) | 0;
    words[words[lengthProperty]] = asciiBitLength;

    for (let j = 0; j < words[lengthProperty];) {
        const w = words.slice(j, j += 16);
        const oldHash = hash.slice(0);

        for (let i = 16; i < 64; i++) {
            const a = w[i - 15];
            const b = w[i - 2];
            w[i] = (
                ((a >>> 7) ^ (a >>> 18) ^ (a >>> 3) ^ (a << 25) ^ (a << 14))
                + ((b >>> 17) ^ (b >>> 19) ^ (b >>> 10) ^ (b << 15) ^ (b << 13))
                + w[i - 7] + w[i - 16]
            ) | 0;
        }

        for (let i = 0; i < 64; i++) {
            const temp = (
                hash[7]
                + ((hash[4] >>> 6) ^ (hash[4] >>> 11) ^ (hash[4] >>> 25) ^ (hash[4] << 26) ^ (hash[4] << 21) ^ (hash[4] << 7))
                + ((hash[4] & hash[5]) ^ (~hash[4] & hash[6]))
                + k[i] + w[i]
            ) | 0;

            const temp2 = (
                ((hash[0] >>> 2) ^ (hash[0] >>> 13) ^ (hash[0] >>> 22) ^ (hash[0] << 30) ^ (hash[0] << 19) ^ (hash[0] << 10))
                + ((hash[0] & hash[1]) ^ (hash[0] & hash[2]) ^ (hash[1] & hash[2]))
            ) | 0;

            hash = [
                (temp + temp2) | 0,
                hash[0],
                hash[1],
                hash[2],
                (hash[3] + temp) | 0,
                hash[4],
                hash[5],
                hash[6]
            ];
        }

        for (let i = 0; i < 8; i++) hash[i] = (hash[i] + oldHash[i]) | 0;
    }

    return hash.map(h =>
        ('00000000' + (h >>> 0).toString(16)).slice(-8)
    ).join('');
}

const prompt = $json.property_prompt || $json.Prompt;
const checksum = $json.property_checksum || $json.Checksum;

const newChecksum = sha256(prompt);

// Main iteration loop for n8n items
return {
    json: {
        pageId: $json.id,
        prompt: prompt,
        oldChecksum: checksum,
        newChecksum: newChecksum,
        needsUpdate: !checksum
          || checksum !== newChecksum
    }
};
